import classes from './Cart.module.css';
import Modal from '../UI/Modal'
import { useContext, useState } from 'react';
import CartContext from '../../store/cart-context';
import CartItem from './CartItem';
import Checkout from './Checkout';

const Cart = (props) => {
  const [checkedout,setCheckout] = useState(false);
  const [isSubmitting,setIsSubmitting] = useState(false);

  const cartCtx = useContext(CartContext);
  const totalAmount = `Rs.${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;

  const cartItemRemoveHandler = id => {};
  const cartItemAddHandler = item => {};

  const orderHandler = () => {

    setCheckout(true);

  };

  const submitOrderHandler = (userData) => {

    setIsSubmitting(true)

    fetch('https://react-food-app-5e29d-default-rtdb.firebaseio.com/orders.json',{
      method: 'POST',
      body: JSON.stringify({
        user: userData,
        orderedItems: cartCtx.items
      })
    });

  };

  const cartItems = (
    <ul className={classes['cart-items']}>
      {cartCtx.items.map((item) => (
        <CartItem key= {item.id} name={item.name} amount={item.amount} price={item.price} 
        onRemove={cartItemRemoveHandler.bind(null, item.id)}
        onAdd= {cartItemAddHandler.bind(null, item)}/>
      ))}
    </ul>
  );

  const modalActions = (
    <div className={classes.actions}>
    <button className={classes['button--alt']} onClick={props.onClose}>Close</button>
    {hasItems && <button className={classes.button}  onClick={orderHandler}>Order</button>}
  </div>
  )

  return (
    <Modal onClose={props.onClose}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      {checkedout && <Checkout onConfirm={submitOrderHandler} onCancel={props.onClose}/>}
      {!checkedout && modalActions}

    </Modal>
  );
};

export default Cart;
