import { useState } from "react";


const Reduceruse = () => {

    const [count,setCount] = useState(0);

    const countHandller = () => {

        setCount(count+1);

        if (count===10){
            const textalert= 'you have reached max limit';
        };
    }

    return (
        <div>
            {<h1> Button is clicked {count} times</h1>}
            <button onClick={countHandller}>Click Here</button>

        </div>
    )
};


export default Reduceruse;
